function cloudNativeBaiduMap(mapData){var map=new BMap.Map("baidu-map-container");map.enableScrollWheelZoom(true);var pointArray=new Array();function switchToCity(e){var p=e.target;var scrollTo=document.getElementById(p.getTitle()).offsetTop-111
document.getElementById("city_list").scrollTop=scrollTo;}
for(var i=0;i<mapData.length;i++){var data=mapData[i];var point=new BMap.Point(data.longitude,data.latitude);var marker=new BMap.Marker(point);var label=new BMap.Label(data.title,{offset:new BMap.Size(-10,20)});marker.setLabel(label);marker.setTitle(data.code);marker.addEventListener("click",switchToCity);map.addOverlay(marker);pointArray[i]=point;}
map.setViewport(pointArray);}