function cloudNativeBaiduMap(mapData){var map=new BMap.Map("baidu-map-container");map.enableScrollWheelZoom(true);var pointArray=new Array();function switchToCity(e){var p=e.target;if(p instanceof BMap.Marker){$(".city-introduction").hide();$("#"+p.getTitle()).show();}}
for(var i=0;i<mapData.length;i++){var data=mapData[i];var point=new BMap.Point(data.longitude,data.latitude);var marker=new BMap.Marker(point);var label=new BMap.Label(data.name,{offset:new BMap.Size(-10,20)});marker.setLabel(label);marker.setTitle(data.code);marker.addEventListener("click",switchToCity);map.addOverlay(marker);pointArray[i]=new BMap.Point(data.longitude,data.latitude);}
map.setViewport(pointArray);$(".city-introduction").hide();$(".showByDefault").show();}